import { useState } from 'react';
import React from 'react'

export default function Form ({addContact}) {
  const [form,setForm] = useState({name:'',number:''})
  const clickBtn = ()=>{
    setForm({...form,name:document.getElementById('name').value,number:document.getElementById('number').value})
    console.log(form);
  }
  return (
    <div>
        <div><input id='name' name='name' placeholder='Enter a Name'/></div>
        <div><input id='number' name='number' placeholder='Enter a number'/></div>
        <div><button onClick={clickBtn}>Add Person</button></div>
    </div>
  )
}
