import { useState } from 'react';
import React from 'react'

export default function Form ({contacts}) {
  const [form,setForm] = useState({name:'',number:''})
  const onChangeInput = (e)=>{
        setForm({...Form,name:e.target.value,number:e.target.value});
        
        
  }
  const onSubmit = (e)=>{
      e.preventDefault();
      console.log(form);
      contacts.push(form);

  }
  return (
    <div>
        <div><input id='name' name='name' placeholder='Enter a Name' onChange={onChangeInput}/></div>
        <div><input id='number' name='number' placeholder='Enter a number'/></div>
        <div><button onClick={onSubmit}>Add Person</button></div>
    </div>
  )
}
