import { useState } from 'react';
import React from 'react'

export default function Form (props) {
  const [form,setForm] = useState({name:'',number:''})
  const onChangeInput = ()=>{
        setForm(previousState=>{
          return({...previousState,name:document.getElementById('name').value,number:document.getElementById('number').value})});
  }
  const onSubmit = (e)=>{
      e.preventDefault();
      console.log(form);
      props.contacts.push(form);
      console.log(props.contacts);
      

  }
  return (
    <div>
        <div><input id='name' name='name' placeholder='Enter a Name' onChange={onChangeInput}/></div>
        <div><input id='number' name='number' placeholder='Enter a number' onChange={onChangeInput}/></div>
        <div><button onClick={onSubmit}>Add Person</button></div>
    </div>
  )
}
