import { useState } from 'react';
import './App.css';

function App() {
  const [points,setPoints] = useState([]);
  const handleCreateClick=(e)=>{
    setPoints((points)=>{
      return [...points,{
        x:e.clientX,
        y:e.clientY
      }];
    })
  }
  return (
    <div className="App" onClick={handleCreateClick}>
      <div className='buttons'>
        <button>Undo</button>
        <button>Redo</button>
        <button>Random</button>
      </div>
      <div className='point'>
        {
          points.map((point,index)=>{
            <div key={index} style={{left:point.x, top:point.y}}></div>
          })
        }
      </div>

    </div>
  );
}

export default App;
