import { useState } from 'react';
import './App.css';

function App() {
  const [points,setPoints] = useState([]);
  const [isUndo,setIsUndo] = useState(false);
  function undoBtn(t){

    const newArr = [...points];
    const removedPoint = newArr.pop();
    setIsUndo(t);
    if(isUndo){
      setPoints(newArr);
    }
    else{
      setPoints((points)=>{
        return [...points,removedPoint];
      })
    }



  }
  const eventObj = {
    type: 'click',
    target: 'button'
  };

  const randomBtn =(e)=>{
    e.stopPropagation();
    const maxX = window.innerWidth;
    const maxY = window.innerHeight;
    setPoints((points)=>{
      return [...points,{
        x:Math.floor(Math.random(0,1)*maxX),
        y:Math.floor(Math.random(0,1)*maxY)
      }]
    })
  }

  const handleCreateClick=(e)=>{
    console.log(e.clientX);
    console.log(e.clientY);
    setPoints((points)=>{
      return [...points,{
        x:e.clientX,
        y:e.clientY
      }];
    })
  }
  return (
    <div className="App" onClick={handleCreateClick}>
      <div className='buttons'>
        <button onClick={undoBtn(true)} >Undo</button>
        <button onClick={undoBtn(false)} >Redo</button>
        <button onClick={randomBtn}>Random</button>
      </div>

        {
          points.map((point,index)=>{
            return (
            <div className='point' key={index} style={{left:point.x, top:point.y}}></div>
            )
          })
        }


    </div>
  );
}

export default App;
