import { useState } from 'react';
import './App.css';

function App() {
  const [points,setPoints] = useState([]);
  const undoBtn=()=>{
    const newArr = [...points];
        newArr.pop();
    setPoints(newArr);
  }
  const randomBtn =(e)=>{
    e.stopPropagation();
    const maxX = window.innerWidth;
    const maxY = window.innerHeight;
    setPoints((points)=>{
      return [...points,{
        x:Math.floor(Math.random(0,1)*maxX),
        y:Math.floor(Math.random(0,1)*maxY)
      }]
    })
  }
  console.log(window.innerHeight);
  console.log(document.body.clientHeight);
  const handleCreateClick=(e)=>{
    console.log(e.clientX);
    console.log(e.clientY);
    setPoints((points)=>{
      return [...points,{
        x:e.clientX,
        y:e.clientY
      }];
    })
  }
  return (
    <div className="App" onClick={handleCreateClick}>
      <div className='buttons'>
        <button onClick={undoBtn} >Undo</button>
        <button>Redo</button>
        <button onClick={randomBtn}>Random</button>
      </div>

        {
          points.map((point,index)=>{
            return (
            <div className='point' key={index} style={{left:point.x, top:point.y}}></div>
            )
          })
        }


    </div>
  );
}

export default App;
