import React from 'react'
import './Card.css';
import { useState,useEffect } from 'react';
import Detail from '../Detail/Detail.js';

function Card({recipeLabel,imgSrc}) {
  const [openDetail,setOpenDetail] = useState(false);
  
  
  return (
    <div className='Card' onClick={()=> setOpenDetail(true)}>
        <img src={imgSrc.SMALL.url}/>
        <p>{recipeLabel}</p>
        {openDetail&&<Detail recipeLabel={recipeLabel} imgSrc={imgSrc.REGULAR.url} closeDetail={setOpenDetail}/>}
    </div>
  )
}

export default Card
