    <div className='Detail'>
      <h2>{recipeLabel}</h2>
      <FontAwesomeIcon className='xIcon' icon={faXmark} onClick={()=> closeDetail(false)}/>
      <button onClick={changeData}>aaaa</button>
      <img src={imgSrc}/>
      <div className='ingredient'>

      </div>

    </div>
  )
}

export default Detail
