import React from 'react'
import { useState,useEffect } from 'react';
import Card from './Card';
import Pages from '../pages/Pages';


function Popular({type}) {
  const getDishType =()=>{
    const url = window.location.href;
    const urlParts = url.split('/');
    const text = urlParts[urlParts.length - 1];
    if(text===" "){
      return "Starter";
    }
    else if(text==="dessert"){
      return "Desserts";
    }
    else if(text==="beverage"){
      return "Drinks";
    }
    else{
      return "Soup";
    }
  }
  const [recipes,setRecipes] = useState([])
  useEffect(()=>{
    getRecipe(getDishType());

  },[Pages])

  
  const getRecipe= async (type)=>{
    //const REACT_API_KEY ="e7704039751459e8dcd58cbf1daabddc"; //edamam api
    const REACT_API_KEY= "a97356d88ede40dd9168e38e3bf2c694"; // spooncular api
    const dataArr =[];
    try{
      const response = await fetch(`https://api.spoonacular.com/recipes/random?apiKey=${REACT_API_KEY}number=12&type=${type}`);
      const data = await response.json();
      // for(let i=0;i<9;i++){
      //     dataArr.push(data.hits[i]);
      // }
      setRecipes(data);
      console.log(data);
      console.log(recipes);
    }
    catch(e){
      console.log(e);
    }
    
  }
  
  return (
    <div>
      {recipes.map((recipe,index)=>{
        return(
          <div key={index}>{recipe}</div>
        )
      })}
    </div>
  )
}

export default Popular;
