import React from 'react';
import './Detail.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark} from '@fortawesome/free-solid-svg-icons';
import { useState,useEffect } from 'react';

function Detail({recipeLabel,imgSrc,closeDetail,setCloseDetail}) {
  
  useEffect(() => {
    const handleOutsideClick = (e) => {
      if (e.target.classList.contains('detail')) {
        setCloseDetail(); // Parent bileşendeki state'i güncellemek için onClose fonksiyonunu çağırın
      }
    };

    document.addEventListener('mousedown', handleOutsideClick);

    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, [setCloseDetail]);
  
  return (
    <div className='detailBack'>
      <div className='Detail'>
      <h2>{recipeLabel}</h2>
      <FontAwesomeIcon className='xIcon' icon={faXmark} />
      <button onClick={setCloseDetail}>aaaa</button>
      <img src={imgSrc}/>
      <div className='ingredient'>

      </div>

    </div>
    </div>
    
  )
}

export default Detail
