import React from 'react'
import './Card.css';
import { useState,useEffect } from 'react';
import Detail from '../Detail/Detail.js';

function Card({recipe,recipeLabel,imgSrc}) {
  const [openDetail,setOpenDetail] = useState(false);
  
  const toggleDetail=()=>{
    setOpenDetail(!openDetail);
  }
  console.log(openDetail);
  return (
    <div className='Card' onClick={toggleDetail}>
        <img src={recipe.recipe.images.SMALL}/>
        <p>{recipe.recipe.label}</p>
        {openDetail&&<Detail recipeLabel={recipeLabel} imgSrc={imgSrc.REGULAR.url} closeDetail={toggleDetail}/>}
    </div>
  )
}

export default Card
