import React from 'react';
import './Detail.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark} from '@fortawesome/free-solid-svg-icons';

function Detail({recipeLabel,imgSrc,extended,setExtended}) {
  const handleOnClick=()=>{
    setExtended(false);
  }
  return (
    <div className='Detail'>
      <h2>{recipeLabel}</h2>
      <FontAwesomeIcon className='xIcon' icon={faXmark} onClick={handleOnClick} />
      <img src={imgSrc}/>
      <div className='ingredient'>

      </div>

    </div>
  )
}

export default Detail
