import React from 'react'
import './Card.css';
import { useState,useEffect } from 'react';
import Detail from '../Detail/Detail.js';

function Card({recipeLabel,imgSrc}) {
  const [extended,setExtended] = useState(false);
  const handleCardClick =()=>{
    setExtended(true);
  }
  useEffect(()=>{
    handleDataChange();
    console.log(extended);
  },[])
  const handleDataChange=(value)=>{
     setExtended(value)
  }
  return (
    <div className='Card' onClick={handleCardClick}>
        <img src={imgSrc.SMALL.url}/>
        <p>{recipeLabel}</p>
        {extended&&<Detail recipeLabel={recipeLabel} imgSrc={imgSrc.REGULAR.url} onDataChange={handleDataChange}/>}
    </div>
  )
}

export default Card
