import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import Card from './Card';
import axios from 'axios';

function Popular() {
  const API_KEY = "e7704039751459e8dcd58cbf1daabddc";
  const API_ID = "af124d04";
  const [recipe,setRecipe] = useState([]);
  useEffect(()=>{
    setRecipe(getRecipe());
  },[])
  const getRecipe= async ()=>{
    try{
      const response = await fetch(`https://api.edamam.com/api/recipes/v2?type=public&q=popular&app_id=${API_ID}&app_key=${API_KEY}`);
      const data = await response.json();
      return data
    }
    catch(e){
      console.log(e);
    }
    
    
    //console.log(response.data.hits);
    setRecipe(response.data.hits);
    
  }
  const a=recipe[0];
  return (
    <div>
        {a.recipe.label}
    </div>
  )
}

export default Popular;
